package com.test;

public class Escape {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		//backslash
		System.out.println("She said \\Hello!\" to me.");
		//feed
		System.out.println("She said \f Hello! to me.");
		// carriage return
		System.out.println("She said \rHello!\" to me.");
		//tab
		System.out.println("She said \tHello!\" to me.");
		// backspace
		System.out.println("She said \bHello!\" to me.");
		
	}

}
