package com.test;

public class Switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Day = "Sunday";
	
	switch (Day) 
		{
				case "Monday": System.out.println("Boring Monday");
							   break;
				case "Sunday": System.out.println("happy Sunday");
							   break;
				case "friday": System.out.println("party friday");			   
							 break;
				default : System.out.println("Your data is Invalid");
				break;
		}
	
	}

}
